import { cartActionType } from './cart.type'

const INITIAL_STATE = {
    hidden: true,
    cartItems: []
}


const cartREducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case cartActionType.INITIAL_STATE:
            return {
                ...state,
                hidden: !state.hidden
            }

        case cartActionType.ADD_ITEM:
            return {
                ...state,
                cartItems: [...state, action.payload]
            }

        default:
            return state
    }


}

export default cartREducer;
