import cartActiontype from './cart.type'


const INITIAL_STATE = {
    hidden: true
}


const cartReduce = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case cartActiontype.TOGGEL_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden

            }
        default:
            return state

    }

}

export default cartReduce;
