import { cartActiontype } from './cart.type'


const INITIAL_STATE = {
    hidden: true
}


export const cartReduce = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case cartActiontype.TOGGEL_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden

            }
        default:
            return state

    }

}
