import React, { useContext } from 'react';

import './chekoutitem.scss';


import { CartContext } from '../../provider/cart/cartProvider';

const ChekoutItem = ({ cartItem }) => {
    const { name, imageUrl, price, quantity } = cartItem;
    const { addItem, removeItems, clearItemFromCart } = useContext(CartContext)
    return (
        <div className='checkout-item'>
            <div className='image-container'>
                <img src={imageUrl} alt='item' />
            </div>
            <span className='name'>{name}</span>
            <span className='quantity'>
                <div className='arrow' onClick={() => removeItems(cartItem)}>
                    &#10094;
            </div>
                <span className='value'>{quantity}</span>
                <div className='arrow' onClick={() => addItem(cartItem)}>
                    &#10095;
            </div>
            </span>
            <span className='price'>{price}</span>
            <div className='remove-button' onClick={() => clearItemFromCart(cartItem)}>
                &#10005;
          </div>
        </div>
    );
}
export default ChekoutItem;
