import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { ReactComponent as Logo } from '../../assest/logo.svg';
import './header.scss';
import { auth } from '../../firebase.utils';
import { connect } from 'react-redux';
import CartIcon from '../CartIcon/CartIcon';
import CartDropDown from '../CartDropDown/CartDropDown';
import { setCurrentUser } from '../../redux/user/user.action';
import UserContext from '../../contexts/users/users.context';
import CartToggleContext from '../../contexts/CartToggle/CartToggle.context';



const Header = () => {

    const currentUser = useContext(UserContext)
    const [hidden, setHidden] = useState(true);
    const toggleHidden = () => setHidden(!hidden)

    return (

        <div className='header'>

            <Link to='/' className='logo-container'>
                <Logo className='logo' />
            </Link>



            <div className='options'>

                <CartToggleContext.Provider value={{ hidden, toggleHidden }}>
                    <CartIcon />


                </CartToggleContext.Provider>



                <Link className='option' to='/shop'> Shop</Link>
                <Link className='option' to="/test"> Contact</Link>


                {

                    currentUser ?
                        <div className="option" onClick={() => auth.signOut()}>  Sign Out </div>
                        :
                        <Link className="option" to="/signin"> Sign In </Link>
                }


            </div>

            {

                hidden ? null : <CartDropDown />
            }




        </div>
    )
}


const mapStateToProps = ({ cart: { hidden } }) => ({


    hidden

})






export default connect(mapStateToProps)(Header);
