import { cartActionType } from './cart.type'

const INITIAL_STATE = {
    hidden: true
}


const cartREducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case cartActionType.INITIAL_STATE:
            return {
                ...state,
                hidden: !state.hidden
            }


        default:
            return state
    }


}

export default cartREducer;
